import {FaTimes} from 'react-icons/fa';

const Task = ({task, deleteTask}) => {
    return (
        <div className='task'>
            <h3>{task.text} <FaTimes onClick={deleteTask} style={{color: 'red', cursor: 'pointer'}}/> </h3>
            <p>{task.day}</p>
            <p>{task.reminder}</p>
        </div>
    )
}

export default Task;
